import React ,{useEffect, useState}from 'react';
import { View, Text, TextInput, Picker, Button, StyleSheet } from 'react-native';
import { Formik } from 'formik';
import DatePicker from 'react-native-datepicker';
import request from '../../common/Request';
import axios from 'axios';
import { URL_API } from '../../common/Constant';
import Autocomplete from 'react-native-autocomplete-input';

const MyForm = () => {
const [comptes , setComptes] = useState([]);
const [natures , setNatures] = useState([]);
const [compteQuery, setCompteQuery] = useState('');
const [selectedCompte, setSelectedCompte] = useState(null);

    useEffect(()=>{
         request('/compte/list/select','GET')
        .then(res=> {
            console.log('API reponse :',res);
            setComptes(res.data);
        } )
        .catch(error => console.error('error compte list :', error));

        request('/nature-opportunite/list/select','get')
        .then(res=> {
          console.log('API reponse :',res);
            setNatures(res.data);
        } )
        .catch(error => console.error('error nature opportunite :', error));

        //request('/compte/list/select','get')
      //  .then(res=> {
          //  console.log('API reponse :',res);
           // setComptes(res.data);
       // } )
        //.catch(error => console.error('error compte list :', error));

    },[])
    const findComptes = () => {
        if (compteQuery === '') {
          return [];
        }
    
        return comptes.filter((compte) => compte.code.toLowerCase().includes(compteQuery.toLowerCase()));
      };

      const renderCompteItem = ({ item }) => (
        <Text>{item.code}</Text>
      );
    
    
  const handleSubmit = (values) => {

    console.log('Données soumises :', values);
  };
  

  return (
    <View style={{backgroundColor:'#00000'}}>
    <Formik
      initialValues={{
        compte: '',
        bu: '',
        sujet: '',
        description: '',
        nature: '',
        type: '',
        niveauEng: '',
        dateRemise: '',
        dateClosing: '',
        rdvEtabli: '',
        CAestime : '',
        marge : '' ,
        margeestimee : '' , 
        planAction: '',
        risques: '',
        pointsFort: '',
      }}
      onSubmit={handleSubmit}
      
    >
    
      {({ handleChange, handleSubmit, values }) => (
        <View>
           <Text>Compte :</Text>
            <Autocomplete
              data={findComptes()}
              value={compteQuery}
              onChangeText={(text) => setCompteQuery(text)}
              renderItem={renderCompteItem}
              keyExtractor={(item) => item.id.toString()}
              onSubmitEditing={() => {
                if (comptes.length > 0) {
                  const selected = comptes.find((compte) => compte.code.toLowerCase() === compteQuery.toLowerCase());
                  setSelectedCompte(selected);
                  handleChange('compte')(selected ? selected.code : ''); // Update Formik value
                }
              }}
            />
            {selectedCompte && <Text>Selected Compte: {selectedCompte.code}</Text>}

          <Text>BU :</Text>
         <Picker selectedValue={values.bu} onValueChange={handleChange('bu')}>
         <Picker.Item label="Logiciels d’Infrastructure" value="logiciels_infrastructure" />
         <Picker.Item label="Cybersécurité" value="cybersecurite" />
         <Picker.Item label="Service" value="service" />
         <Picker.Item label="Solutions Endpoint" value="solutions_endpoint" />
         <Picker.Item label="Cloud et Datacenter" value="cloud_datacenter" />
         </Picker>

          <Text>Sujet :</Text>
          <TextInput style value={values.sujet} onChangeText={handleChange('sujet')} multiline numberOfLines={6} />
          <Text>Description :</Text>
          <TextInput value={values.description} onChangeText={handleChange('description')} multiline numberOfLines={6} />

          <Text>Nature :</Text>
           <Picker selectedValue={values.nature} onValueChange={handleChange('nature')}>
           {natures.map(nature => (
            <Picker.Item key={nature.id} label={nature.libelle} value = {nature.value} />
           ))}
          </Picker>


          <Text>Type :</Text>
          <Picker selectedValue={values.type} onValueChange={handleChange('type')}>
            <Picker.Item label="Autre" value="autre" />
            <Picker.Item label="RFI" value="rfi" />
            <Picker.Item label="RFP" value="rfp" />
          </Picker>

          <Text>Niveau Eng :</Text>
          <Picker selectedValue={values.niveauEng} onValueChange={handleChange('niveauEng')}>
            <Picker.Item label="Engagement" value="engagement" />
            <Picker.Item label="Opportunité" value="opportunite" />
          </Picker>

          <Text>Date Remise :</Text>
          <DatePicker
            style={{ width: 200 }}
            date={values.dateRemise}
            mode="date"
            placeholder="Sélectionnez la date"
            format="YYYY-MM-DD"
            confirmBtnText="Confirmer"
            cancelBtnText="Annuler"
            onDateChange={(date) => handleChange('dateRemise')(date)}
          />
          <Text>Date closing :</Text>
          <DatePicker
            style={{ width: 200 }}
            date={values.dateClosing}
            mode="date"
            placeholder="Sélectionnez la date"
            format="YYYY-MM-DD"
            confirmBtnText="Confirmer"
            cancelBtnText="Annuler"
            onDateChange={(date) => handleChange('dateClosing')(date)}
          />
           <Text>CA estimé :</Text>
           <TextInput 
           keyboardType="numeric"
           value={values.CAestime}
           onChangeText={handleChange('CAestime')}
         />
         <Text>Marge :</Text>
         <View style={{flexDirection:'row',alignItems:'center'}}>
           <TextInput 
           keyboardType="numeric"
           value={values.marge}
           onChangeText={handleChange('marge')}
         />
        <Text>%</Text>
       </View>  
         <Text>Marge estimé :</Text>
         <Text>{(values.CAestime*(values.marge/100)).toFixed(2)}</Text>

          <Text>Plan d’Action :</Text>
          <TextInput value={values.planAction} onChangeText={handleChange('planAction')} multiline numberOfLines={4} />

          <Text>Risques :</Text>
          <TextInput value={values.risques} onChangeText={handleChange('risques')} multiline numberOfLines={3} />

          <Text>Points Forts :</Text>
          <TextInput value={values.pointsFort} onChangeText={handleChange('pointsFort')} multiline numberOfLines={3} />

          <Button title="Soumettre" onPress={handleSubmit} />
        </View>
      )}
    </Formik>
    </View>
  );
};

export default MyForm;


const styles = StyleSheet.create({})
