import React ,{useEffect, useState , useRef}from 'react';
import { View, Text, TextInput, Picker, Button, StyleSheet,Platform, TouchableOpacity, dropdownController } from 'react-native';
import { Formik } from 'formik';
import DatePicker from 'react-native-datepicker';
import request from '../../common/Request';




const MyForm = () => {
const [comptes , setComptes] = useState([]);
const [natures , setNatures] = useState([]);
const [types , setTypes] = useState([]);
const [niveau , setNiveau] = useState([]);
const [create , setCreate] = useState([]);




    useEffect(()=>{
         request('/compte/list/select','GET')
        .then(res=> {
            console.log('API reponse compte :',res.data);
            const data=res.data.map(e=> { return { title:e.raisonSociale,id:e.code}})
            setComptes(data);

        } )
        .catch(error => console.error('error compte list :', error));

        request('/nature-opportunite/list/select','get')
        .then(res=> {
          console.log('API reponse :',res);
            setNatures(res.data);
        } )
        .catch(error => console.error('error nature opportunite :', error));

        request('/type-opportunite/list/select','get')
        .then(res=> {
            console.log('API reponse :',res);
            setTypes(res.data);
        } )
        .catch(error => console.error('error types list :', error));

        request('/niveau-engagement/list/select','get')
        .then(res=> {
            console.log('API reponse :',res);
            setNiveau(res.data);
        } )
        .catch(error => console.error('error Niveau engagement :', error));
//  request('/opportunite/create','post')
//        .then(res=> {
//            console.log('API reponse :',res);
//             setCreate(res.data);
//         } )
//         .catch(error => console.error('error creation :', error));



    },[])

    
    const handleSubmit = async (values) => {
        try {
           console.log("values",values)
         //  return;
        //  const valuess={
        //     compte:{code:"C10100"}
        //     ,busConcernes:[{"id":3},{"id":2}],
        //     "sujet":"test opportunité 200",
        //     "description":"opportunité de test 200",
        //     "nature":{"code":"ACQUISITION"},
        //     "type":{"code":"RFI"},"niveauEngagement":{"code":"ENGAGEMENT"},"dateRemise":"2022-05-20T15:31:00.120Z","dateClosing":"2022-06-30T23:59:59.680Z","caEstime":870000,"pctgMargeEstimee":40,"margeEstimee":348000,"weekPlaning":"description plan d'action ...","risques":"description risque ...","pointsForts":"description points forts ..."
        //  }
 
          const response = await request('/opportunite/create', 'POST',values);
      
          console.log('API response:', response);
      
          if (response.status >= 200 && response.status < 300) {
        
            console.log('Opportunity created successfully!');
          } else {
           
            console.error('Unexpected status code:', response.status);
            console.error('Response body:', response.data);
          }
        } catch (error) {
          console.error('Error creating opportunity:', error);
        
          if (error.response) {
            console.error('Error response:', error.response.data);
            console.error('Status code:', error.response.status);
          } else if (error.request) {
           
            console.error('No response received:', error.request);
          } else {
            
            console.error('Error message:', error.message);
          }
        }
      };
      
  return (
    <View >
    <Formik
      initialValues={{
        compte: {code:''},
        bu: [{id:''}] ,
        sujet: '',
        description: '',
        nature: {code:''},
        type: {code:''},
        niveauEngagement: {code:''},
        dateRemise: '',
        dateClosing: '',
        rdvEtabli: '',
        CAestime : '',
        marge : '' ,
        margeestimee : '' , 
        planAction: '',
        risques: '',
        pointsFort: '',
      }}
      onSubmit={ (values, actions) => {
         handleSubmit(values)
         actions.resetForm()
       }}
      
    > 
      {({ handleChange, handleSubmit, values }) => (
        <View style={{backgroundColor:'#BFD0D8'}} >
           <Text style={{color:'#0384C2'}}>Compte :</Text>
           <Picker selectedValue={values.compte} onValueChange={handleChange('compte.code')}>
           {comptes.map(compte => (
            <Picker.Item  key={compte.id} label={compte.id} value = {compte.id} />
           ))}
          </Picker>
          <Text style={{ color: '#0384C2' }}>BU :</Text>
<Picker
  selectedValue={values.bu}
  onValueChange={handleChange('bu[0].id')}
  mode="dropdown" // Set the mode to dropdown
  style={{ height: 50, width: 200 }}
  selectMultiple // Enable multiple selection
  enabled // Enable the picker
>
  <Picker.Item label="Logiciels d’Infrastructure" value={4} />
  <Picker.Item label="Cybersécurité" value={3} />
  <Picker.Item label="Service" value={8} />
  <Picker.Item label="Solutions Endpoint" value={5} />
  <Picker.Item label="Cloud et Datacenter" value={2} />
</Picker>

          <Text style={{color:'#0384C2'}}>Sujet :</Text>
          <TextInput style value={values.sujet} onChangeText={handleChange('sujet')} multiline numberOfLines={6} />
          <Text style={{color:'#0384C2'}}>Description :</Text>
          <TextInput value={values.description} onChangeText={handleChange('description')} multiline numberOfLines={6} />

          <Text style={{color:'#0384C2'}}>Nature :</Text>
           <Picker selectedValue={values.nature} onValueChange={handleChange('nature.code')}>
           {natures.map(nature => (
            <Picker.Item key={nature.id} label={nature.libelle} value = {nature.value} />
           ))}
          </Picker>

          <Text style={{color:'#0384C2'}}>Types :</Text>
           <Picker selectedValue={values.type} onValueChange={handleChange('type.code')}>
           {types.map(type => (
            <Picker.Item  key={type.id} label={type.code} value = {type.value} />
           ))}
          </Picker>

          <Text style={{color:'#0384C2'}}>Niv engagement :</Text>
           <Picker selectedValue={values.niveau} onValueChange={handleChange('niveauEngagement.code')}>
           {niveau.map(niveau => (
            <Picker.Item  key={niveau.id} label={niveau.code} value = {niveau.value} />
           ))}
          </Picker>
          <Text style={{color:'#0384C2'}}>Date Remise :</Text>
          <DatePicker
            style={{ width: 200 }}
            date={values.dateRemise}
            mode="date"
            placeholder="Sélectionnez la date"
            format="YYYY-MM-DD"
            confirmBtnText="Confirmer"
            cancelBtnText="Annuler"
            onDateChange={(date) => handleChange('dateRemise')(date)}
          />
          <Text style={{color:'#0384C2'}}>Date closing :</Text>
          <DatePicker
            style={{ width: 200 }}
            date={values.dateClosing}
            mode="date"
            placeholder="Sélectionnez la date"
            format="YYYY-MM-DD"
            confirmBtnText="Confirmer"
            cancelBtnText="Annuler"
            onDateChange={(date) => handleChange('dateClosing')(date)}
          />
           <Text style={{color:'#0384C2'}}>CA estimé :</Text>
           <TextInput 
           keyboardType="numeric"
           value={values.CAestime}
           onChangeText={handleChange('CAestime')}
         />
         <Text style={{color:'#0384C2'}}>Marge :</Text>
         <View style={{flexDirection:'row',alignItems:'center'}}>
           <TextInput 
           keyboardType="numeric"
           value={values.marge}
           onChangeText={                    
                            handleChange('marge')
                           
                         }
         />
        <Text>%</Text>
       </View>  
         <Text style={{color:'#0384C2'}}>Marge estimé :</Text>
         <Text onChangeText={handleChange('CAestime')} >{(values.CAestime*(values.marge/100)).toFixed(2)}</Text>

          <Text style={{color:'#0384C2'}}>Plan d’Action :</Text>
          <TextInput value={values.planAction} onChangeText={handleChange('planAction')} multiline numberOfLines={4} />

          <Text style={{color:'#0384C2'}}>Risques :</Text>
          <TextInput value={values.risques} onChangeText={handleChange('risques')} multiline numberOfLines={3} />

          <Text style={{color:'#0384C2'}}>Points Forts :</Text>
          <TextInput value={values.pointsFort} onChangeText={handleChange('pointsFort')} multiline numberOfLines={3} />

          <Button  title="Enrengistrer" onPress={handleSubmit } />
        </View>
      )}
    </Formik>
    </View>
  );
};

export default MyForm;


const styles = StyleSheet.create({})
